import React,{useState} from 'react';
import daegu from '../../../images/daegu.svg'
import './FloatingButton.css'
import ModalDandi from './ModalDandi';
import ModalInfo from './ModalInfo'
  
const FloatingButton = () => {

    //플로팅버튼 on/off
    const [isOpen, setMenu] = useState(false);  // 메뉴의 초기값을 false로 설정
    const toggleMenuOpen = () => {
            if(isOpen) { 
                setMenu(false);
            } else {
                setMenu(true);
            }
    }

    //Dandi모달창 on/off
    const [modalDandi, setModalDandi] = useState('close');
    const toggleModalDandi = () => {
        if(modalDandi=='close') {
            setModalDandi('open');
        } else {
            setModalDandi('close');
        }
    }    


    //Info모달창 on/off
    const [modalInfo, setModalInfo] = useState('close');
    const toggleModalInfo = () => {
        if(modalInfo=='close') {
            setModalInfo('open');
        } else {
            setModalInfo('close');
        }
    }   

    

  return(
        <div className="floatingBtn">
            <ul className={isOpen ? "show-menu" : "hide-menu"}> 
                <li ><div className='list' onClick={toggleModalDandi}>Dandi란?</div></li>
                <li ><div className='list'  onClick={toggleModalInfo}>이용안내</div></li>
            </ul>
            <img id="fabPic" src={daegu} onClick={()=>toggleMenuOpen()}/>      

            { modalDandi=='close' ? null : <ModalDandi toggleModalDandi={toggleModalDandi}/>}
            { modalInfo=='close' ? null : <ModalInfo  toggleModalInfo={toggleModalInfo}/> }             
        </div>
    )
}

export default FloatingButton;
