import { useState } from 'react';
import ToggleModal from './ToggleModal';
function Modal(props) {

     ///이 방법 아닌거 같지만.. 눈속임ㅠ
     const [state, setState] = useState({
        center: { lat: 0, lng: 0 },
        isPanto: true,
      });

     const [modal1, setModal1] = useState(false);  
     const toggleModal1 = (lat,lng) => {
         setModal1(!modal1);
         setState({
             center: { lat, lng },
             isPanto: true,
         })
     }  
     const [modal2, setModal2] = useState(false);
     const toggleModal2 = (lat,lng) => {
         setModal2(!modal2)
         setState({
             center: { lat, lng },
             isPanto: true,
         })
     }  
     const [modal3, setModal3] = useState(false);
     const toggleModal3 = (lat,lng) => {
         setModal3(!modal3)
         setState({
             center: { lat, lng },
             isPanto: true,
         })
     }  
     const [modal4, setModal4] = useState(false);
     const toggleModal4 = (lat,lng) => {
         setModal4(!modal4)
         setState({
             center: { lat, lng },
             isPanto: true,
         })
     }  
     const [modal5, setModal5] = useState(false);
     const toggleModal5 = (lat,lng) => {
         setModal5(!modal5)
         setState({
             center: { lat, lng },
             isPanto: true,
         })
     }  
     const [modal6_1, setModal6_1] = useState(false);
     const toggleModal6_1 = (lat,lng) => {
         setModal6_1(!modal6_1)
         setState({
             center: { lat, lng },
             isPanto: true,
         })
     }  

    return(
        <div className="modal">
        <div className="overlay"></div>
        <div className="modal-content">
       <div className="modalTitleWrap">
            <div className="mapModalTitle">{props.data[props.i].name}</div>
            <div className='closeModal' onClick={props.toggleModal1}></div>
        </div>  
        <div className="modalWrap1">
        <div className="modalWrap">
            <img id="modalPic" src={props.data[props.i].picture_path}/>
            <p className="modalTxt">·{props.data[props.i].description}</p>
            <p className="modalTxt">·주소:{props.data[props.i].address}</p>
            <div className='locationInfo' onClick={() => toggleModal1(props.data[props.i].latitude, props.data[props.i].longitude)}>위치안내</div>
            </div> 
        </div>
        </div>
        {   modal1? <ToggleModal toggleModal1={toggleModal1}/>: null }
    </div>
    )
}

export default Modal;
