import { useState } from 'react';
import SubModal from './SubModal';

function Modal(props) {

    //submodal창 ㅐ
    const [modal1_1, setModal1_1] = useState(false); 
    const [modal2_1, setModal2_1] = useState(false);
    const [modal3_1, setModal3_1] = useState(false);
    const [modal4_1, setModal4_1] = useState(false);
    const [modal5_1, setModal5_1] = useState(false);
    const [modal6_1, setModal6_1] = useState(false);


    //위치 상태 저장
    const [state, setState] = useState({
       center: { lat: 0, lng: 0 },
       isPanto: true,
     });
     const locationInfo = (lat,lng) => {
        setState({
            center: { lat, lng },
            isPanto: true,
        });
        setHeader(false);
    }  

    const [header, setHeader] = useState(true);
    return(
        <div className="modal">
        <div className="overlay"></div>
        <div className="modal-content">
        { header
        ? <div className="modalTitleWrap">
            <div className="mapModalTitle">{props.data[props.i].name}</div>
            <div className='closeModal' onClick={props.closeModal}></div>
        </div>  
        : null}

        <div className="modalWrap1">
        <div className="modalWrap">
            <img id="modalPic" src={props.data[props.i].picture_path}/>
            <p className="modalTxt">·{props.data[props.i].description}</p>
            <p className="modalTxt">·주소:{props.data[props.i].address}</p>
            <div className='locationInfo' onClick={() => locationInfo(props.data[props.i].latitude, props.data[props.i].longitude)}>위치안내</div>
            </div> 
        </div>
        </div>
        {  modal1_1? <SubModal i={0} center={state.center} closeModal={props.closeModal}/>: null }
        {  modal2_1? <SubModal i={1} center={state.center} closeModal={props.closeModal}/>: null }
        {  modal3_1? <SubModal i={2} center={state.center} closeModal={props.closeModal}/>: null }
        {  modal4_1? <SubModal i={3} center={state.center} closeModal={props.closeModal}/>: null }
        {  modal5_1? <SubModal i={4} center={state.center} closeModal={props.closeModal}/>: null }
        {  modal6_1? <SubModal i={5} center={state.center} closeModal={props.closeModal}/>: null }
    </div>
    )
}

export default Modal;
