        {  modal4_1? <SubModal i={3} center={state.center} closeModal={props.closeModal}/>: null }
        {  modal5_1? <SubModal i={4} center={state.center} closeModal={props.closeModal}/>: null }
        {  modal6_1? <SubModal i={5} center={state.center} closeModal={props.closeModal}/>: null } */}
    </div>
    )
}

export default Modal;
